import React, { useState } from 'react';
import TextField from '@mui/material/TextField';
import Autocomplete from '@mui/material/Autocomplete';

/*
const DADATA_TOKEN = '077c2359913a904470c191acdb2795ab64204c5f';
*/
const DADATA_TOKEN = 'b64560af54b95583ac2711f8ecb9744514f08b11';

export default function DaDataAutocomplete({
  type='address',
  label='Адрес',
  initialQuery='Бел',
  initialValue,
  onChange=(event, newValue) => {console.log(newValue);},
  inputProps
}) {
  const [suggestions, setSuggestions] = useState([]);
  const [inputValue, setInputValue] = useState(initialQuery);

  const handleInputChange = async (event, newInputValue) => {
    console.log(newInputValue)
    newInputValue = event.target.value;
    setInputValue(newInputValue);
    if (newInputValue) {
      try {
        const response = await fetch('https://suggestions.dadata.ru/suggestions/api/4_1/rs/suggest/' + type, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Token ' + DADATA_TOKEN
          },
          body: JSON.stringify({
            query: newInputValue
          })
        });
        const data = await response.json();
        setSuggestions(data.suggestions);
      } catch (error) {
        console.log(error);
      }
    } else {
      setSuggestions([]);
    }
  }

  const handleChange = async (event, newValue) => {
    onChange(event, newValue);
  }

  return (
    <Autocomplete
      freeSolo={true}
      filterOptions={(x) => x}
      options={suggestions}
      getOptionLabel={(option) => option.value}
      onInputChange={handleInputChange}
      onFocus={handleInputChange}
      onChange={handleChange}
      inputValue={inputValue}
      {...inputProps}
      renderInput={(params) => <TextField {...params} label={label} />}
    />
  );
}
