import React, { useState } from 'react';
import TextField from '@mui/material/TextField';
import Stack from '@mui/material/Stack';
import Autocomplete from '@mui/material/Autocomplete';

const DADATA_TOKEN = '077c2359913a904470c191acdb2795ab64204c5f';

export default function FreeSolo() {
  const [suggestions, setSuggestions] = useState([]);

  const handleInputChange = async (event) => {
    const inputValue = event.target.value;
    if (inputValue) {
      try {
        const response = await fetch('https://suggestions.dadata.ru/suggestions/api/4_1/rs/suggest/address', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Token ' + DADATA_TOKEN
          },
          body: JSON.stringify({
            query: inputValue
          })
        });
        const data = await response.json();
	console.log(data);
        setSuggestions(data.suggestions.map(suggestion => suggestion.value));
      } catch (error) {
        console.log(error);
      }
    } else {
      setSuggestions([]);
    }
  };

  const highlightMatch = (option, inputValue) => {
    const regex = new RegExp(inputValue, 'gi');
    const parts = option.split(regex);
    return (
      <span>
        {parts.map((part, index) => (
          <span key={index}>
            {part}
            {index !== parts.length - 1 && <b>{inputValue}</b>}
          </span>
        ))}
      </span>
    );
  };

  return (
    <Autocomplete
      renderInput={(params) => <TextField {...params} label='Адрес' />}
      freeSolo
      filterOptions={(x) => x}
      options={suggestions}
      onInputChange={handleInputChange}
      renderOption={(option) => highlightMatch(option, inputValue)}
    />
  );
}
